definition.script:
pipeline{
    agent any

    environment{
        GIT_BRANCH = "main"
        GITHUB_TOKEN = "token"
}
    stages{
        stage('Clone repository'){
            steps{
                echo "Github branch is ${env.GIT_BRANCH}"
                echo "Github branch is ${env.GITHUB_TOKEN}"

                git branch: 'main', credentialsId: 'jenkinsCreatedCred', url: 'https://token@github.com/org/repo'
            }
        }

        stage('release'){
            steps{
                sh "npm install @semantic-release/git -D"
                sh "npm install @semantic-release/changelog -D"
                sh "npm install semantic-release-helm -D"
                sh "npm install @semantic-release/exec -D"
                sh "npm install semantic-release-yaml -D"
                sh "npx semantic-release"
            }
        }
    }

}
